#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        map<ll, ll> previousResults;
        ll ans = 0;

        int n = nums.size();
        for (int i = 0; i < n; i++) {

            long long target = k;
            if (nums[i] == target) 
                ans++;

            map<ll, ll> currentResults;
            currentResults[nums[i]] = 1;
           for (auto it = previousResults.begin(); it != previousResults.end(); it++) {
                long long value = it->first;
                long long frequency = it->second;

                long long newAndResult = value & nums[i];
                if (newAndResult == target) {
                    ans += frequency;
                }

                currentResults[newAndResult] += frequency;
            }
            previousResults = currentResults;
        }

        return ans;
    }

};
