class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;

        unordered_map<long long, long long>prev;

        for(int i=0;i<n;i++)
        {
            unordered_map<long long,long long> currRes;

            if(nums[i]==k){
                ++count;
            }
            currRes[nums[i]]=1;

            for(auto& [val,freq]: prev)
            {
                long long nar=val&nums[i];
                if(nar==k) count+=freq;
                currRes[nar]+=freq;
            }

            prev=currRes;
        }
        return count;
    }
};
