class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<long long, long  long> mp;

        for(int i=0;i<nums.size();i++){
            unordered_map<long long, long long> curr;
            long long num = nums[i];
            if(num==k)ans++;
            curr[num]=1;

            for(auto i:mp){
                long long x = (i.first & num);
                if(x==k)ans+=i.second;
                curr[x]+=i.second;
            }

            mp=curr;
        }

        return ans;
    }
};
