class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        d=defaultdict(lambda:0)
        ans=0
        for i in nums:
            curr=defaultdict(lambda:0)
            for j in list(d.keys()):
                curr[j&i]+=d[j]
            curr[i]+=1
            if k in curr:
                ans+=curr[k]
            d=curr
            # print(d,ans)
        return ans
