class Solution {
public:
    long long countSubarrays(vector<int>& array, int k) {
         int p = array.size();
        long long val = 0;
        unordered_map<long long, long long>skm;

        for (int i = 0; i < p; i++) {

            unordered_map<long long, long long> pk; 
            if (array[i] == k) 
                ++val;

            pk[array[i]] = 1;
            for (auto& [vk, frq] :skm) {
                long long ppp = vk & array[i];
                if (ppp == k) 
                    val += frq;

                pk[ppp] += frq;
            }

            skm = pk;
        }
        return val;

    }
};
