class Solution {
public:
    using ll = long long;
    long long countSubarrays(vector<int>& nums, int k) {
        std::unordered_map<int, int> mp;
        ll ans{0};
        for (int it : nums) {
            std::unordered_map<int, int> mp2;
            if (it == k) {
                ans++;
            }
            mp2[it]++;
            for (auto& [key, val] : mp) {
                int result = key & it;
                if (result == k) {
                    // cout << "val " << val << "; key=" << key << "; it=" << it << endl;
                    ans += val;
                    mp2[result] += val;
                } else {
                    mp2[result] += val;
                }

            }
            mp = mp2;
        }

        return ans;
    }
};
