#define ll long long
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        ll res = 0;
        unordered_map<ll, ll>fre;
        for (auto &x:nums) {
            unordered_map<ll, ll>cfre; 
            cfre[x]=1;
            for (auto&f:fre) {
                ll av =f.first&x;
                if(av==k)res+=f.second;
                cfre[av]+=f.second;
            }
            fre =cfre;
        }
        for(auto &x:nums)res+=(x==k);
        return res;
    }
};
