class Solution {
public:
    long long findSubarrayCount(vector<int>& arr, int targetAND) {
        long long resultCount = 0;
        unordered_map<int, int> currentANDCount;
        int i = 0;

        while (i < arr.size()) {
            int element = arr[i];
            unordered_map<int, int> tempANDCount;
            tempANDCount[element]++;

            for (const auto& it : currentANDCount) {
                int newANDValue = it.first & element;
                tempANDCount[newANDValue] += it.second;
            }

            for (const auto& it : tempANDCount) {
                if (it.first == targetAND) {
                    resultCount += it.second;
                }
            }
            currentANDCount = tempANDCount;
            i++;
        }
        return resultCount;
    }

    long long countSubarrays(vector<int>& arr, int targetAND) {
        return findSubarrayCount(arr, targetAND);
    }
};
