class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        sz = len(nums)
        ans = 0
        mpp = defaultdict(int)

        for i in range(sz):
            mpp2 = defaultdict(int)
            if nums[i] == k:
                ans += 1
            mpp2[nums[i]] = 1

            for v, f in mpp.items():
                curr = v & nums[i]
                if curr == k:
                    ans += f
                mpp2[curr] += f

            mpp = mpp2

        return ans
