class Solution {
public:

    long long countSubarrays(vector<int>& arr, int k) {
    long long int n = arr.size(),ans=0,sum=0;
    unordered_map<long long, long long> mp1;
    map<int,int> mp;

    for(int i=0;i<n;i++)
    {
        sum+=arr[i];
    }

    for(int i=0;i<n;i++)
    {
        mp[arr[i]]=i;
    }

    int counter=0;

    while(counter<n)
    {
        unordered_map<long long, long long> mp2;

        mp2[arr[counter]] = 1;
        ans+=(arr[counter]==k);

        for(auto it : mp1)
        {
            long long int First=it.first,Second=it.second;

            long long int temp=First&arr[counter];

            ans+=(temp==k)?(Second):0;

            mp2[temp]+=Second;
        }

        counter++;

        mp1=mp2;
    }

    return ans;
    }
};
