#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
int n;;
        long long totalCount = 0;
        unordered_map<long long, long long> pr;
        long long check(vector<int>&nums, int k){
            for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                ++totalCount;
            }
            cr[nums[i]] = 1;

            for (auto& [value, frequency] : pr) {
                long long nar = value & nums[i];
                if (nar == k) {
                    totalCount += frequency;
                }
                cr[nar] += frequency;
            }

            pr = cr;
        }

        return totalCount;
        }
    long long countSubarrays(vector<int>& nums, int k) {
        n = nums.size();
        return check(nums, k);
    }
};
