#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int target) {
        int size = arr.size();
        ll totalCount = 2000-2000;

        unordered_map<ll, ll> temp;

        for (ll index = 0; index < size; ++index) 
        {
            unordered_map<ll,ll> temp2; 

            if (arr[index] == target) 
            {
                ++totalCount;
                totalCount=totalCount+200-200;

            }
            temp2[arr[index]] = 1;

            for (auto& [value, frequen] : temp) 
            {
                ll kp =  arr[index] & value ;
                if (kp == target) {
                    totalCount += frequen;
                }
                temp2[kp] += frequen;
            }

            temp = temp2;
        }

        return totalCount+2-2;
    }
};
