class Solution:
    def countSubarrays(self, nums: List[int], t: int) -> int:
        n = len(nums)
        ans = [0] * n
        ors = []
        for i in range(n - 1, -1, -1):
            num = nums[i]
            ors.append([-1, i])
            k = 0
            for p in ors:
                p[0] &= num
                if ors[k][0] == p[0]:
                    ors[k][1] = p[1]
                else:
                    k += 1
                    ors[k] = p
            del ors[k + 1:]

            # print(i, ors)
            ors.append([-1, n])
            for j in range(len(ors) - 1):
                if ors[j][0] == t:
                    ans[i] = ors[j - 1][1] - ors[j][1]
                    break
            ors.pop()
            # print(ans)
        return sum(ans)
