class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        unordered_map<long long,long long> p_map;
        for(int i=0;i<n;i++){
             unordered_map<long long,long long> c_map;
             if(nums[i]==k){
                 ans++;
             }
             c_map[nums[i]]=1;

             for(auto it : p_map){
                 long long new_=it.first & nums[i];
                 if(new_ ==k){
                     ans=1ll*ans+it.second;
                 }
                 c_map[new_]+=it.second;
             }

             p_map=c_map;
        }

        return ans;
    }
};
