class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long counts = 0;

        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 

            if (nums[i] == k) {
                ++counts;
            }
            curr[nums[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newA = val & nums[i];
                if (newA == k) {
                    counts += freq;
                }
                curr[newA] += freq;
            }

            prev = curr;
        }

        return counts;
    }
};
