class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        map<long long, long long> mp;
        map<long long,long long> freqq;
        for(auto it: nums) {
            ans += (it == k);
            for(auto itx : mp) {
                long long req = (itx.first&it);
                if(req == k) ans += itx.second;
                freqq[req] += itx.second;
            }
            if(freqq.find(it) != freqq.end()) freqq[it] += 1;
            else freqq[it] = 1;
            mp = freqq;
            freqq.clear();
        }
        return ans;
    }
};
