class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans =0;
        vector< int >hash(32,0);
        int i=1;
        for(auto e:nums){
            for(int i=0;i<32;i++){
                if((1<<i)&e){
                    hash[i]++;
                }
                else{
                    hash[i]=0;
                }
            }
            set<int>st1;
            set<int>st2;
            for(int i=0;i<32;i++){
                if((1<<i)&k){
                    st1.insert(hash[i]);
                }
                else{
                    st2.insert(hash[i]);
                }
            }
            if(st1.size()>0){
                ans+=max(0,*st1.begin()-*st2.rbegin());
            }

            if(  k==0){
                ans+=max(0,(i)-*st2.rbegin());
            }
            i++;
        }
        return ans;
    }
};
