typedef long long ll;

class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        ll ans = 0;
        unordered_map<ll, ll>mp1;
        for(int i = 0; i<n; i++){
            unordered_map<ll, ll>mP2;
            if(arr[i] == k){
                ans++;
            }
            mP2[arr[i]] = 1;
            for(auto &[val, freq] : mp1){
                ll nr = val & arr[i];
                if(nr == k){
                    ans += freq;
                }
                mP2[nr] += freq;
            }
            mp1 = mP2;
        }

        return ans;
    }
};
