class Solution {
public:
    int tree[4 * 100001];

// Function which builds the segment tree
void build(vector<int>& arr, int node, int start, int end)
{
    if (start == end) {
        tree[node] = arr[start];
        return;
    }
    int mid = (start + end) >> 1;
    build(arr, 2 * node, start, mid);
    build(arr, 2 * node + 1, mid + 1, end);
    tree[node] = (tree[2 * node] & tree[2 * node + 1]);
}

// Function that returns bitwise OR of segment [L..R]
int query(int node, int start, int end, int l, int r)
{
    if (start > end)
        return 0;
    if(start > r || end < l) {
        return -1;
    }

    if (start >= l && end <= r) {
        return tree[node];
    }

    int mid = (start + end) >> 1;
    int q1 = query(2 * node, start, mid, l, r);
    int q2 = query(2 * node + 1, mid + 1, end, l, r);
    return (q1 & q2);
}

    long long cnt(vector<int>& arr, int n, int K)
{
    long long count = 0;
    for (int i = 0; i < n; i++) {

        // Check for subarrays starting with index i
        long long low = 0, high = i, index = INT_MAX;
        while (low <= high) {

            long long mid = (low + high) >> 1;
            if (query(1, 0, n - 1, mid, i) >= K) {
                index = min(index, mid);

                high=mid-1;
            }
            else {
                low = mid + 1;
            }
        }

        // Increase count with number of subarrays
        //  having OR >= K and starting with index i
        // cout<<index<<' ';
        if (index != INT_MAX) {
            count += i-index+1;
        }
    }
        // cout<<'\n';
    return count;
}
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        build(nums, 1, 0, n-1);
        long long kplus1 = cnt(nums, n, k+1);
        long long k0 = cnt(nums, n, k);
        // cout<<kplus1<<' '<<k0<<'\n';
        return k0-kplus1;
    }
};
