class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        cin.tie(0);
        ios::sync_with_stdio(false);

        vector<vector<int> > z(32);
        vector<int> idxs(32);
        vector<vector<int> > mp(32, vector<int>(nums.size()));
        for(int i=0;i<nums.size();i++){
            for(int j=0;j<32;j++){
                z[j].push_back(i);
                if(nums[i] & (1<<j)){
                    ;
                }
                else {
                    for(;idxs[j]<z[j].size();idxs[j]++){
                        mp[j][z[j][idxs[j]]] = i;
                    }
                }
            }
        }
        for(int i=0;i<32;i++){
            for(;idxs[i]<z[i].size();idxs[i]++){
                mp[i][z[i][idxs[i]]] = nums.size();
            }
        }

        long long ans = 0;
        for(int i=0;i<nums.size();i++){
            if(nums[i] >= k){
                int maxx = INT_MIN, minn = nums.size()-1;
                for(int j=0;j<32;j++){
                    if(k & (1<<j)){
                        minn = min(mp[j][i]-1, minn);
                    }
                    else {
                        maxx = max(mp[j][i], maxx);
                    }
                }
                if(minn >= maxx){
                    ans += minn - maxx + 1;
                }
            }
        }
        return ans;
    }
};
