class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        long long cnt=0;
        map<int,int>mp;
        int xr=nums[0];
        for(int i=0;i<nums.size();i++)
        {
            if(nums[i]==k)cnt++;
           map<int,int> check;
           check[nums[i]]++;
           for(auto x:mp)
           {
            int tobeand=x.first&nums[i];
            if(tobeand==k)cnt+=x.second;
            check[tobeand]+=x.second;
           }
           mp.clear();
           mp=check;

        }
        return cnt;

    }
};
