typedef long long int ll;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<ll, ll> current;
        unordered_map<ll, ll> previous;
        long long ans = 0;


        for(auto i : nums) {
            if (i == k) {
                ans++;
            }
        }
        for (int i = 0; i < nums.size(); i++) 


        {
            current.clear();

            current[nums[i]]=1;


            for (auto& [a, b] : previous) {
                ll new_key = a & nums[i];
                current[new_key] += b;


                if (new_key == k) {
                    ans += b;
                }
            }



            previous = current;
        }

        return ans;
    }
};
