typedef long long pqr;

class Solution {
public:
    pqr countSubarrays(vector<int>& vish, int dev) {
        int vinn = vish.size();
        pqr bunn = 0;map<pqr, pqr> chinn;int p = 0;
        while (p < vinn) {
            map<pqr, pqr> pra;
         if (vish[p] == dev) 
                ++bunn;
            pra[vish[p]] = 1;auto it = chinn.begin();
            while (it != chinn.end()) {
                pqr bha = it->first & vish[p];
                if (bha == dev)
                    bunn += it->second;
                pra[bha] += it->second;
                ++it;
            }chinn = pra;
            ++p;
        }return bunn;
    }
};
