class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<long long int>>dp(32,vector<long long int>(n+1,n));
        for(int i=0;i<=31;i++){
            long long int f=n;
            for(int j=n-1;j>=0;j--){
                if((nums[j]>>i)&1);
                else{
                    f=j;
                }
                dp[i][j]=f;
            }
        }
        long long int ans=0;
        for(int i=0;i<n;i++){
            long long int f=n;
            long long int g=i;
            bool hi=true;
            for(int j=0;j<=31;j++){
                if((k>>j)&1)f=min(f,dp[j][i]);
                else{
                   g=max(g,dp[j][i]);
                }
            }
            ans+=max(0ll,f-g);
            // cout<<ans<<" "<<f<<endl;
        }
        // cout<<n<<endl;
        return ans;
    }
};
