#define ll long long int
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       ll ans = 0;
       map<ll, ll> m;
       for (auto i: nums) {
            map<ll,ll> mp;
            for (auto it : m) {
            ll p = it.first & i;
            mp[p] += it.second;
            if (p == k) {
                ans += it.second;
            }
        }
        mp[i]++;
        m = mp;
        if (i == k) {
            ans++;
        }
    }
    return ans;
    }
};
