class Solution {
public:
    long long countSubarrays(vector<int>& A, int tg) {
        long long tot = 0;
        unordered_map<long long, long long> pr;
        for (int i = 0; i < A.size(); ++i) {
            unordered_map<long long, long long> cr;
            if (A[i] == tg)
                tot++;
            cr[A[i]] = 1;
            for (auto& [val, fq] : pr) {
                long long nR = val & A[i];
                if (nR == tg)
                    tot += fq;
                cr[nR] += fq;
            }
            pr = cr;
        }
        return tot;
    }
};
