from collections import defaultdict

class Solution:
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0
        prev = {}

        for i in range(n):
            temp = {}

            if nums[i] == k:
                count += 1
            if nums[i] in temp:
                temp[nums[i]] += 1
            else:
                temp[nums[i]] = 1

            for val, times in prev.items():
                temp_value = val & nums[i]
                if temp_value == k:
                    count += times
                if temp_value in temp:
                    temp[temp_value] += times
                else:
                    temp[temp_value] = times

            prev = temp

        return count
