class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        l = len(nums)
        count = 0
        d = {}

        for i in range(l):
            d1 = {}

            if nums[i] == k:
                count += 1
            d1[nums[i]] = 1

            for val, freq in d.items():
                cur = val & nums[i]
                if cur == k:
                    count += freq
                d1[cur] = d1.get(cur, 0) + freq

            d = d1

        return count
