typedef long long ll;
class Solution {
public:
    ll countSubarrays(std::vector<int>& nums, int k) {
        int n = nums.size();
        ll count = 0;

        std::unordered_map<ll, ll> previousAndResults;

        for (int i = 0; i < n; ++i) {
            std::unordered_map<ll, ll> LaavedResult;

            if (nums[i] == k) {
                ++count;
            }
            LaavedResult[nums[i]] = 1;

            for (const auto& pair : previousAndResults) {
                ll newAndResult = pair.first & nums[i];
                if (newAndResult == k) {
                    count += pair.second;
                }
                LaavedResult[newAndResult] += pair.second;
            }

            previousAndResults = LaavedResult;
        }

        return count;
    }
};
