#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        ll gun = 0;
        unordered_map<long long, long long> shreyasis;
        for (int it = 0; it < size; it++) {
            unordered_map<ll, long long> akhon; 
            if (nums[it] == k) {
                gun++;
            }
            akhon[nums[it]] = 1;
            for (auto& [value, frequ] : shreyasis) {
                long long neAnd = value & nums[it];
                if (neAnd == k) {
                    gun += frequ;
                }
                akhon[neAnd] += frequ;
            }

            shreyasis = akhon;
        }
        return gun;
    }
};
