class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        unordered_map<int,int> m;
        for(auto &i:nums){
            if((i&k)!=k) m.clear();
            else{
                unordered_map<int,int> m1;
                m1[i]++;
                for(auto &[t,v]:m){
                    m1[t&i]+=v;
                }
                ans+=m1[k];
                m=move(m1);
            }
        }
        return ans;
    }
};
