class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long ans=0;
        int n=a.size();
        vector<queue<int>> dp(32);
        for(int i=0;i<a.size();i++)
        {
            for(int j=0;j<32;j++)
            {
                if(!(a[i]&(1<<j)))dp[j].push(i);
            }
            if(i==(n-1))
            {
                for(int j=0;j<32;j++)dp[j].push(n);
            }
        }

        for(int i=0;i<a.size();i++)
        {
            int mini=i,maxi=n-1;
            for(int j=0;j<32;j++)
            {
                if(dp[j].front()<i)dp[j].pop();

                if(k&(1<<j))maxi=min(maxi,dp[j].front()-1);
                else mini=max(mini,dp[j].front());
            }
            if(maxi>=mini)ans+=(maxi-mini+1);
        }
        return ans;
    }
};
