class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        N = len(nums)


        @cache
        def calc(index, cur):
            if cur < k:
                return 0

            total = 0
            if cur == k:
                total += 1

            if index + 1 == N:
                return total

            total += calc(index + 1, cur & nums[index + 1])

            return total



        total = 0
        for i in range(N):
            total += calc(i, nums[i])
        return total
