class Solution {
public:
long long min(long long a,long long b){
    if(a<b) return a;
    return b;
}
    long long countSubarrays(vector<int>& nums, int k) {
        int t=0;
        int l=0,r=0;
        vector<int>ltz(32,-1);
        for(int i=0;i<32;i++){
            if((nums[0] & (1<<i))==0) ltz[i]=0; 
        }
        long long ans=0;
        while(r<nums.size()){
            if((nums[r]&k)>=k){
                r++;
                long long mx=INT_MAX;
                for(int i=0;i<32;i++){
                    if((k&(1<<i))==0){
                        mx=min(ltz[i],mx);
                    }
                }
                if(mx!=-1){
                    if(mx>=l){
                        ans+=(mx-l+1);
                    }
                }
            }
            else{
                r++;
                l=r;
            }
            if(r==nums.size()) break;
            for(int i=0;i<32;i++){
                if((nums[r] & (1<<i))==0) ltz[i]=r; 
            }            
        }
        return ans;
    }
};
