#define ll long long

class Solution {

public:
    long long countSubarrays(vector<int>& a, int k) {
    ll ans = 0;
    int n = a.size();
    vector<unordered_map<ll, ll>> dp(2);
    int curr = 0, prev = 1;

    for (int i = 0; i < n; ++i) {
        dp[curr].clear();

        if (a[i] == k) {
            ans++;
        }

        for (auto x : dp[prev]) {
            ll newKey = x.first & a[i];
            dp[curr][newKey] += x.second;
            if (newKey == k) {
                ans += x.second;
            }
        }

        dp[curr][a[i]]++;
        swap(curr, prev);
    }

    return ans;
    }
};
