class Solution {
public:
    long long countSubarrays(vector<int>&arr, int t) {
       int n = arr.size();
    long long ans = 0;
    unordered_map<int, long long> prev;
    unordered_map<int, long long> curr;

    for (int i = 0; i < n; ++i) {
        if (arr[i] == t) {
            ++ans;
        }

        curr.clear(); 

        curr[arr[i]] ++; 

        for (auto& pr : prev) {
            int pv = pr.first;
            long long fr = pr.second;

            int na = pv & arr[i]; 
             curr[na] += fr;



            if (na == t) {
                ans += fr; 
            }

        }

        prev.swap(curr);
    }

    return ans;
    }
};
