class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;
        map<long long,long long> prevRes;
        for(int i=0;i<n;i++){
            map<long long ,long long> currRes;

            if(nums[i]==k) ++count;
            currRes[nums[i]]=1;
            for(auto& [val,freq] : prevRes){
                long long newAndRes=val & nums[i];
                if(newAndRes==k){
                    count+=freq;
                }
                currRes[newAndRes]+=freq;
            }
            prevRes=currRes;
        }
        return count;
    }
};
