using ll = long long;
class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        map<int, ll> temp;
        for(auto &num : nums){
            if(num == k) ans++;
            map<int, ll> val;
            val[num] = 1;
            for(auto &it1 : temp){
                int new_val = num & it1.first;
                if(new_val == k) ans += it1.second;
                val[new_val] += it1.second;
            }
            temp = val;
        }
        return ans;
    }
};
