class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        #define int long long
        int ans = 0;
        int n = nums.size();

        map<int , int > m;

        for (int l = 0; l < n; l++) {

            map<int , int > m2;

            int curr = nums[l];

            if(curr == k) ans++;

            m2[curr]++;

            for( auto &i : m){
                int curr2 = i.first;
                int cnt = i.second;

                if((curr & curr2) == k) ans += cnt;

                m2[curr & curr2] += cnt;

            }

            m = m2;



        }

        return ans;
        #undef int
