class Solution {
public:
    bool cond(int a,int n){
        return a==n;
    }
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long ans = 0;   
    int ctr=0;
    for(int i=0;i<n;i++)
        ctr++;
    unordered_map<long long, long long> pq;

    for (int i = 0; i < n; ++i) {
    unordered_map<long long, long long> cq; 
    if (cond(nums[i],k))
    ans++;
    cq[nums[i]] = 1;
    for (auto& [p, q] : pq) {
    long long temp = p & nums[i];
    if (cond(temp, k)) 
    ans += q;
    cq[temp] += q;
    }
    pq = cq;
    }
    return ans-ctr+ctr;
    }
};
