class Solution:
    from collections import defaultdict
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n,res = len(nums),0
        mp = defaultdict(int)

        for i in range(n):
            andMp = defaultdict(int)
            for u, v in mp.items():
                new_and = nums[i] & u
                andMp[new_and] += v
            andMp[nums[i]] += 1
            mp = andMp


            if k in mp:
                res += mp[k]

        return res
