class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k){
      int n=nums.size();
      long long ans = 0;
      unordered_map<long long,long long>mpp;

        for(int i= 0;i<n;i++){
            unordered_map<long long,long long>mpc;
            long long tar = k; 
            if(nums[i] == tar){
                ans++;
            }
            mpc[nums[i]] = 1;
            for(auto & [v,f] : mpp){
                long long res = v & nums[i];                                                                                         
                if (res == tar) {
                    ans += f;
                }
                mpc[res] += f;
            }
            mpp = mpc;
        }

        return ans;
    }
};
