#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& vv, ll k) {
        vector<ll>v;
        for(auto i:vv)v.push_back(i);
        map<ll,ll>mp;
        map<ll,vector<ll>>trk;

        ll ans=0;
        ll x=((ll)1<<32)-1;
        int n=v.size();
        vector<int>t(32);
        int idx=0;
        while(k>0){
            t[idx]=k%2;
            k/=2;
            idx++;
        }
        for(int i=0;i<n;i++){
            x=(x&v[i]);
            vector<int>tt(32);
            ll xx=x;
            int idx=0;
            while(xx>0){
                tt[idx]=xx%2;
                xx/=2;
                idx++;
            }
            vector<int>ttt(32);
            idx=0;
            while(v[i]>0){
                ttt[idx]=v[i]%2;
                v[i]/=2;
                idx++;
            }
            for(int j=0;j<32;j++){
                if(ttt[j]==0){mp[j]=i;trk[j].push_back(i);}
            }
            ll l=0,r=i;
            int flag=0;
            for(int i=0;i<32;i++){
                if(t[i]!=tt[i]){
                    if(t[i]==0)flag++;
                    else
                    {l=max(l,mp[i]+1);}
                }
                if(t[i]==tt[i]){
                   if(t[i]==0){
                       r=min(r,trk[i].back());

                   }
                }

            }

            if(flag)continue;
            // cout<<i<<" "<<endl;
            // cout<<l<<" "<<r<<endl;
            // cout<<r-l+1<<endl;
            ans+=max((ll)0,r-l+1);
        }
        return ans;
    }
};
