import java.util.HashMap;

class Solution {
    public void duplicate(int z,int w){
        int q=0;
        w=z;
        z=w;
        q=w+z;
    }
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long cnt = 0;

        HashMap<Long, Long> p = new HashMap<>();
        int  z=-1;
        int w=1;
        for(int i=0;i<n;i++){
             w++;
            z=z+1;
        }
        duplicate(z,w);
        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> cr = new HashMap<>();

            if (nums[i] == k) {
                ++cnt;
            }
            cr.put((long) nums[i], 1L);

            for (Long val : p.keySet()) {
                long freq = p.get(val);
                long nr = val & nums[i];
                z+=1;
                // k=k-2-z+i;
                if (nr == k) {
                    cnt += freq;
                }
                cr.put(nr, cr.getOrDefault(nr, 0L) + freq);
            }

            p= cr;
        }

        return cnt;
    }
}
