class Solution {
public:
    #define ll long long
    long long countSubarrays(vector<int>& nums, int k)
    {
         ll n = nums.size();
         ll count =0;
        unordered_map<ll,ll> prev;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll,ll> curr; 

            if (nums[i] == k) {
                count++;;
            }
            curr[nums[i]] = 1;

            for (auto& it : prev) {
                ll newAndResult = it.first & nums[i];
                if (newAndResult == k) {
                    count += it.second;
                }
                curr[newAndResult] += it.second;
            }

            prev = curr;
        }

        return count;

    }
};
