#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, unordered_map<int, ll>> f;
        f.clear();
        f[1][nums[0]] = 1;
        ll ans = 0;
        ans += f[1][k];
        for(int i = 2; i <= n; i ++) {
            if((nums[i - 1] & k) == k) {
                for(auto j : f[i - 1]) {
                    // cout << j.first << endl;
                    if(((j.first & nums[i - 1]) & k) == k) {
                        f[i][j.first & nums[i - 1]] += j.second;
                    }
                }
                f[i][nums[i - 1]] += 1;
                ans += f[i][k];
                // cout << i << ' ' << f[i][k] << endl;
            }
        }
        return ans;
    }
};
