class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;
        vector<int> pref(n,0);
        int cnt = 0;
        unordered_map<long long, long long> mp;

        for(int i = 0;i<n;i++){
            unordered_map<long long, long long> mp1;
            if(nums[i] == k){
                res++;
                pref[i]=1;
            }
            mp1[nums[i]] = 1;

            for(auto it : mp){
                long long num = it.first&nums[i];
                if(num == k){
                    res += it.second;
                }
                mp1[num] += it.second; 
                pref[i]++;
            }
            mp = mp1;
            if(pref[i]==k) cnt++;
            res--;
        }


        return res+n;
    }
};
