import java.util.HashMap;
import java.util.Map;

class Solution {
    public long countSubarrays(int[] arr, int k) {
        int n = arr.length;
        long count = 0;
        Map<Long, Long> prevResults = new HashMap<>();

        for (int index = 0; index < n; ++index) {
            Map<Long, Long> currResults = new HashMap<>();

            if (arr[index] == k) {
                ++count;
            }

            currResults.put((long) arr[index], 1L);

            for (Map.Entry<Long, Long> entry : prevResults.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();

                long newResult = val & arr[index];

                if (newResult == k) {
                    count += freq;
                }

                currResults.put(newResult, currResults.getOrDefault(newResult, 0L) + freq);
            }

            prevResults = currResults;
        }

        return count;
    }
}
