#define sid_ll long long

class Solution {
public:
    sid_ll countSubarrays(vector<int>& sid_nums, int sid_k) {
        sid_ll sid_n = sid_nums.size(), sid_ANS = 0;

        map<int,int> sid_mp;

        for(int sid_i = 0; sid_i < sid_n; sid_i++)
        {
            map<int,int> sid_cp;

            if(sid_nums[sid_i] == sid_k)
                sid_ANS++;

            sid_cp[sid_nums[sid_i]] = 1;

            for(auto &sid_x: sid_mp)
            {
                sid_ll sid_res = sid_x.first & sid_nums[sid_i];
                if(sid_res == sid_k)
                    sid_ANS += sid_x.second;
                sid_cp[sid_res] += sid_x.second;
            }

            sid_mp = sid_cp;
        }

        return sid_ANS;
    }
};
