/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
     let n = nums.length;
    let ans = 0;

    let prev = new Map();

    for (let i = 0; i < n; ++i) {
        let curr = new Map();

        if (nums[i] === k) {
            ++ans;
        }
        curr.set(nums[i], 1);

        for (let [val, freq] of prev) {
            let newAndResult = val & nums[i];
            if (newAndResult === k) {
                ans += freq;
            }
            curr.set(newAndResult, (curr.get(newAndResult) || 0) + freq);
        }

        prev = curr;
    }

    return ans;
};
